import React, { useState, useRef } from "react";

const AddUserModal = ({ onClose, fetchUsers }) => {
  const [name, setName] = useState("");
  const [email, setEmail] = useState("");
  const [age, setAge] = useState("");
  const [address, setAddress] = useState("");

  const nameRef = useRef(null);
  const emailRef = useRef(null);
  const ageRef = useRef(null);
  const addressRef = useRef(null);

  const handleAddUser = async () => {
    // 유효성 체크
    if (!name || !email || !age || !address) {
      alert("All fields are required!");
      if (!name) nameRef.current.focus();
      else if (!email) emailRef.current.focus();
      else if (!age) ageRef.current.focus();
      else if (!address) addressRef.current.focus();
      return;
    }

    try {
      const response = await fetch("https://672c8aef1600dda5a9f8de2e.mockapi.io/api/v1/users", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({ name, email, age, address }),
      });

      if (!response.ok) {
        throw new Error("Failed to add user");
      }

      fetchUsers(); // 사용자가 추가되면 목록을 다시 가져옴
      onClose(); // 모달 닫기
    } catch (error) {
      console.error("Error adding user:", error);
      alert("Failed to add user. Please try again.");
    }
  };

  return (
    <div>
      <h2>Add User</h2>
      <input
        ref={nameRef}
        value={name}
        onChange={(e) => setName(e.target.value)}
        placeholder="Name"
      />
      <input
        ref={emailRef}
        value={email}
        onChange={(e) => setEmail(e.target.value)}
        placeholder="Email"
        type="email"
      />
      <input
        ref={ageRef}
        value={age}
        onChange={(e) => setAge(e.target.value)}
        placeholder="Age"
        type="number"
      />
      <input
        ref={addressRef}
        value={address}
        onChange={(e) => setAddress(e.target.value)}
        placeholder="Address"
      />
      <button onClick={handleAddUser}>Add</button>
      <button onClick={onClose}>Close</button>
    </div>
  );
};

export default AddUserModal;
