import React, { useState, useEffect, useRef } from "react";
import { useParams, useNavigate } from "react-router-dom";

const UpdatePage = () => {
  const { id } = useParams(); // URL에서 사용자 ID 가져옴
  const navigate = useNavigate(); // 페이지 이동을 위한 훅

  const [user, setUser] = useState({ name: "", email: "", age: "", address: "" }); // 사용자 데이터 상태
  const [editCount, setEditCount] = useState(0); // 총 수정 횟수
  const nameRef = useRef(null); // 이름 입력창 포커스 관리

  // 사용자 데이터 가져오기
  useEffect(() => {
    const fetchUser = async () => {
      try {
        const response = await fetch(`https://672c8aef1600dda5a9f8de2e.mockapi.io/api/v1/users/${id}`);
        const data = await response.json();
        setUser(data);
      } catch (error) {
        console.error("Error fetching user:", error);
      }
    };

    fetchUser();
  }, [id]);

  // 입력값 변경 핸들러 (API 호출 포함)
  const handleChange = async (e) => {
    const { name, value } = e.target;
    setUser((prev) => ({ ...prev, [name]: value }));
    setEditCount((prev) => prev + 1); // 수정 횟수 증가

    try {
      // PUT API 호출
      await fetch(`https://672c8aef1600dda5a9f8de2e.mockapi.io/api/v1/users/${id}`, {
        method: "PUT",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({ ...user, [name]: value }),
      });
    } catch (error) {
      console.error("Error updating user:", error);
    }
  };

  // 컴포넌트 마운트 후 이름 입력창에 포커스 설정
  useEffect(() => {
    if (nameRef.current) {
      nameRef.current.focus();
    }
  }, []);

  return (
    <div className="container mt-4">
      <h2>Edit User</h2>
      <form>
        <div className="mb-3">
          <label className="form-label">Name</label>
          <input
            ref={nameRef}
            type="text"
            name="name"
            className="form-control"
            value={user.name}
            onChange={handleChange}
          />
        </div>
        <div className="mb-3">
          <label className="form-label">Email</label>
          <input
            type="email"
            name="email"
            className="form-control"
            value={user.email}
            onChange={handleChange}
          />
        </div>
        <div className="mb-3">
          <label className="form-label">Age</label>
          <input
            type="number"
            name="age"
            className="form-control"
            value={user.age}
            onChange={handleChange}
          />
        </div>
        <div className="mb-3">
          <label className="form-label">Address</label>
          <input
            type="text"
            name="address"
            className="form-control"
            value={user.address}
            onChange={handleChange}
          />
        </div>
      </form>
      <p>Total Edits: {editCount}</p>
      <button className="btn btn-secondary mt-3" onClick={() => navigate("/list")}>
        Back to List
      </button>
    </div>
  );
};

export default UpdatePage;
