import React, { useState, useEffect } from "react";
import { useNavigate } from "react-router-dom";

const UpdatePage = () => {
  const [user, setUser] = useState({ name: "", email: "", age: "", address: "" });
  const [editCount, setEditCount] = useState(0); // 수정 횟수 추적
  const navigate = useNavigate();

  // 페이지 로딩 시 기존 데이터 가져오기
  useEffect(() => {
    const fetchUser = async () => {
      try {
        const response = await fetch("https://672c8aef1600dda5a9f8de2e.mockapi.io/api/v1/users/1"); // 임시로 ID 1번 데이터 가져옴
        const data = await response.json();
        setUser({
          name: data.name || "",
          email: data.email || "",
          age: data.age || "",
          address: data.address || "",
        });
      } catch (error) {
        console.error("Error fetching user:", error);
      }
    };

    fetchUser();
  }, []);

  // 실시간 데이터 업데이트 핸들러
  const handleInputChange = async (field, value) => {
    const updatedUser = { ...user, [field]: value };
    setUser(updatedUser);

    try {
      const response = await fetch(
        `https://672c8aef1600dda5a9f8de2e.mockapi.io/api/v1/users/1`, // 임시로 ID 1번 데이터 수정
        {
          method: "PUT",
          headers: { "Content-Type": "application/json" },
          body: JSON.stringify(updatedUser),
        }
      );

      if (!response.ok) {
        throw new Error("Failed to update user");
      }

      // 수정 횟수 증가
      setEditCount((prevCount) => prevCount + 1);
    } catch (error) {
      console.error("Error updating user:", error);
    }
  };

  return (
    <div className="container">
      <header>
        <h1>🕊️ Pieace Member 🕊️</h1>
      </header>
      <div className="update-form">
        <h2>Update User</h2>
        <div>
          <label>Name:</label>
          <input
            type="text"
            value={user.name}
            onChange={(e) => handleInputChange("name", e.target.value)}
            placeholder=" 이름"
          />
        </div>
        <div>
          <label>Email:</label>
          <input
            type="email"
            value={user.email}
            onChange={(e) => handleInputChange("email", e.target.value)}
            placeholder="이메일"
          />
        </div>
        <div>
          <label>Age:</label>
          <input
            type="number"
            value={user.age}
            onChange={(e) => handleInputChange("age", e.target.value)}
            placeholder="나이"
          />
        </div>
        <div>
          <label>Address:</label>
          <input
            type="text"
            value={user.address}
            onChange={(e) => handleInputChange("address", e.target.value)}
            placeholder="주소"
          />
        </div>
        <p>Total Edits: {editCount}</p>
        <button onClick={() => navigate("/list")}>Back to List</button>
      </div>
    </div>
  );
};

export default UpdatePage;
