import React, { useState, useEffect } from "react";
import { useParams, useNavigate } from "react-router-dom";

const UpdatePage = () => {
  const { id } = useParams(); // URL에서 사용자 ID 가져오기
  const navigate = useNavigate(); // 페이지 이동을 위한 훅
  const [user, setUser] = useState({ name: "", email: "", age: "", address: "" });
  const [editCount, setEditCount] = useState(0); // 수정 횟수

  // 사용자 정보 가져오기
  useEffect(() => {
    const fetchUser = async () => {
      try {
        const response = await fetch(`https://672c8aef1600dda5a9f8de2e.mockapi.io/api/v1/users/${id}`);
        const data = await response.json();
        setUser(data);
      } catch (error) {
        console.error("Error fetching user:", error);
      }
    };
    fetchUser();
  }, [id]);

  // 입력값 변경 시 업데이트
  const handleChange = (e) => {
    const { name, value } = e.target;
    setUser({ ...user, [name]: value });
    setEditCount((prev) => prev + 1); // 수정 횟수 증가
  };

  // 사용자 업데이트 요청
  const handleUpdate = async () => {
    try {
      const response = await fetch(`https://672c8aef1600dda5a9f8de2e.mockapi.io/api/v1/users/${id}`, {
        method: "PUT",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify(user),
      });
      if (!response.ok) throw new Error("Failed to update user");
      navigate("/list"); // 성공 시 메인 페이지로 이동
    } catch (error) {
      console.error("Error updating user:", error);
    }
  };

  return (
    <div className="container">
      <h2>Update User</h2>
      <form>
        <label>Name:</label>
        <input
          type="text"
          name="name"
          value={user.name}
          onChange={handleChange}
        />
        <label>Email:</label>
        <input
          type="email"
          name="email"
          value={user.email}
          onChange={handleChange}
        />
        <label>Age:</label>
        <input
          type="number"
          name="age"
          value={user.age}
          onChange={handleChange}
        />
        <label>Address:</label>
        <input
          type="text"
          name="address"
          value={user.address}
          onChange={handleChange}
        />
      </form>
      <p>Total Edits: {editCount}</p>
      <button onClick={handleUpdate}>Update</button>
      <button onClick={() => navigate("/list")}>Cancel</button>
    </div>
  );
};

export default UpdatePage;
