import React, { useState, useEffect } from "react";
import { useParams, useNavigate } from "react-router-dom";

const DetailPage = () => {
  const { id } = useParams(); // URL에서 사용자 ID 가져옴
  const navigate = useNavigate(); // 페이지 이동을 위한 훅
  const [user, setUser] = useState(null); // 사용자 데이터 상태

  // 사용자 데이터 가져오기
  useEffect(() => {
    const fetchUser = async () => {
      try {
        const response = await fetch(`https://672c8aef1600dda5a9f8de2e.mockapi.io/api/v1/users/${id}`);
        const data = await response.json();
        setUser(data);
      } catch (error) {
        console.error("Error fetching user:", error);
      }
    };

    fetchUser();
  }, [id]);

  // 데이터 로딩 중 상태 표시
  if (!user) {
    return <p>Loading...</p>;
  }

  return (
    <div className="container mt-4">
      <h2>User Details</h2>
      <div className="card">
        <div className="card-body">
          <h5 className="card-title">{user.name}</h5>
          <p className="card-text"><strong>Email:</strong> {user.email}</p>
          <p className="card-text"><strong>Age:</strong> {user.age}</p>
          <p className="card-text"><strong>Address:</strong> {user.address}</p>
        </div>
      </div>
      <button className="btn btn-secondary mt-3" onClick={() => navigate("/list")}>
        Back to List
      </button>
    </div>
  );
};

export default DetailPage;
