import React, { useState, useEffect } from "react";

const EditUserModal = ({ user, onClose, fetchUsers }) => {
  const [name, setName] = useState("");
  const [email, setEmail] = useState("");
  const [age, setAge] = useState("");
  const [address, setAddress] = useState("");
  const [editCount, setEditCount] = useState(0); // 수정 횟수 추적

  useEffect(() => {
    if (user) {
      setName(user.name || "");
      setEmail(user.email || "");
      setAge(user.age || "");
      setAddress(user.address || "");
    }
  }, [user]);

  // 수정 이벤트 핸들러
  const handleInputChange = async (field, value) => {
    if (!user) return;

    const updatedUser = { ...user, [field]: value };

    try {
      const response = await fetch(
        `https://672c8aef1600dda5a9f8de2e.mockapi.io/api/v1/users/${user.id}`,
        {
          method: "PUT",
          headers: { "Content-Type": "application/json" },
          body: JSON.stringify(updatedUser),
        }
      );

      if (!response.ok) {
        throw new Error("Failed to update user in real-time");
      }

      // 수정 횟수 증가
      setEditCount((prevCount) => prevCount + 1);
      fetchUsers(); // 사용자 목록 갱신
    } catch (error) {
      console.error("Error updating user in real-time:", error);
      alert("Failed to update user in real-time. Please try again.");
    }

    // 상태 업데이트
    if (field === "name") setName(value);
    if (field === "email") setEmail(value);
    if (field === "age") setAge(value);
    if (field === "address") setAddress(value);
  };

  return (
    <div className="modal">
      <div className="modal-content">
        <h2>Edit User</h2>
        <div>
          <label>Name:</label>
          <input
            type="text"
            value={name}
            onChange={(e) => handleInputChange("name", e.target.value)}
            placeholder="Name"
          />
        </div>
        <div>
          <label>Email:</label>
          <input
            type="email"
            value={email}
            onChange={(e) => handleInputChange("email", e.target.value)}
            placeholder="Email"
          />
        </div>
        <div>
          <label>Age:</label>
          <input
            type="number"
            value={age}
            onChange={(e) => handleInputChange("age", e.target.value)}
            placeholder="Age"
          />
        </div>
        <div>
          <label>Address:</label>
          <input
            type="text"
            value={address}
            onChange={(e) => handleInputChange("address", e.target.value)}
            placeholder="Address"
          />
        </div>
        <p>Total Edits: {editCount}</p>
        <button className="close" onClick={onClose}>
          Close
        </button>
      </div>
    </div>
  );
};

export default EditUserModal;
