import React, { useState, useEffect } from "react";
import AddUserModal from "../components/Modals/AddUserModal";
import EditUserModal from "../components/Modals/EditUserModal";
import UserList from "../components/UserList";

const ListPage = () => {
  const [users, setUsers] = useState([]);
  const [isAddModalOpen, setIsAddModalOpen] = useState(false);
  const [isEditModalOpen, setIsEditModalOpen] = useState(false);
  const [selectedUser, setSelectedUser] = useState(null);

  const fetchUsers = async () => {
    try {
      const response = await fetch("https://example.mockapi.io/users");
      const data = await response.json();
      setUsers(data);
    } catch (error) {
      console.error("Error fetching users:", error);
    }
  };

  useEffect(() => {
    fetchUsers();
  }, []);

  const handleDelete = async (id) => {
    try {
      await fetch(`https://example.mockapi.io/users/${id}`, {
        method: "DELETE",
      });
      fetchUsers();
    } catch (error) {
      console.error("Error deleting user:", error);
      alert("Failed to delete user.");
    }
  };

  const handleEdit = (user) => {
    setSelectedUser(user);
    setIsEditModalOpen(true);
  };

  return (
    <div className="container">
      <header>üïäÔ∏è Pieace Member üïäÔ∏è</header>
      <button className="btn" onClick={() => setIsAddModalOpen(true)}>
        Add User
      </button>
      <UserList users={users} onDelete={handleDelete} onEdit={handleEdit} />
      {isAddModalOpen && (
        <AddUserModal onClose={() => setIsAddModalOpen(false)} fetchUsers={fetchUsers} />
      )}
      {isEditModalOpen && selectedUser && (
        <EditUserModal
          user={selectedUser}
          onClose={() => setIsEditModalOpen(false)}
          fetchUsers={fetchUsers}
        />
      )}
    </div>
  );
};

export default ListPage;
