import React, { useState, useEffect, useRef } from "react";
import { useParams, useNavigate } from "react-router-dom";

const UpdatePage = () => {
  const { id } = useParams();
  const navigate = useNavigate();
  const [user, setUser] = useState({ name: "", email: "", age: "", address: "" });
  const [editCount, setEditCount] = useState(0);

  const nameRef = useRef();
  const emailRef = useRef();
  const ageRef = useRef();
  const addressRef = useRef();

  useEffect(() => {
    const fetchUser = async () => {
      try {
        const response = await fetch(`https://672c8aef1600dda5a9f8de2e.mockapi.io/api/v1/users/${id}`);
        const data = await response.json();
        setUser(data);
      } catch (error) {
        console.error("Error fetching user:", error);
      }
    };
    fetchUser();
  }, [id]);

  const handleChange = async (e) => {
    const { name, value } = e.target;
    setUser((prev) => ({ ...prev, [name]: value }));
    setEditCount((prev) => prev + 1);

    try {
      await fetch(`https://672c8aef1600dda5a9f8de2e.mockapi.io/api/v1/users/${id}`, {
        method: "PUT",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({ ...user, [name]: value }),
      });
    } catch (error) {
      console.error("Error updating user:", error);
    }
  };

  const validateInput = () => {
    if (!nameRef.current.value || !emailRef.current.value) {
      alert("Name and Email are required!");
      return false;
    }
    return true;
  };

  const handleSubmit = () => {
    if (validateInput()) navigate("/list");
  };

  return (
    <div className="container">
      <h2 className="text-center">Update User</h2>
      <form>
        <div className="mb-3">
          <label className="form-label">Name</label>
          <input
            ref={nameRef}
            type="text"
            name="name"
            className="form-control"
            value={user.name}
            onChange={handleChange}
          />
        </div>
        <div className="mb-3">
          <label className="form-label">Email</label>
          <input
            ref={emailRef}
            type="email"
            name="email"
            className="form-control"
            value={user.email}
            onChange={handleChange}
          />
        </div>
        <div className="mb-3">
          <label className="form-label">Age</label>
          <input
            ref={ageRef}
            type="number"
            name="age"
            className="form-control"
            value={user.age}
            onChange={handleChange}
          />
        </div>
        <div className="mb-3">
          <label className="form-label">Address</label>
          <input
            ref={addressRef}
            type="text"
            name="address"
            className="form-control"
            value={user.address}
            onChange={handleChange}
          />
        </div>
        <p>Total Edits: {editCount}</p>
        <button type="button" className="btn btn-success me-2" onClick={handleSubmit}>
          Save
        </button>
        <button type="button" className="btn btn-danger" onClick={() => navigate("/list")}>
          Cancel
        </button>
      </form>
    </div>
  );
};

export default UpdatePage;
