import React, { useEffect, useState } from "react";
import { useParams } from "react-router-dom";

const DetailPage = () => {
  const { id } = useParams();
  const [user, setUser] = useState(null);

  const fetchUserDetail = async () => {
    try {
      const response = await fetch(`https://672c8aef1600dda5a9f8de2e.mockapi.io/api/v1/users/${id}`);
      const data = await response.json();
      setUser(data);
    } catch (error) {
      console.error("Error fetching user detail:", error);
    }
  };

  useEffect(() => {
    fetchUserDetail();
  }, [id]);

  if (!user) return <p>Loading...</p>;

  return (
    <div className="list_con">
      <h2>User Detail</h2>
      <p><strong>üôãüèªName:</strong> {user.name}</p>
      <p><strong>üíåEmail:</strong> {user.email}</p>
      <p><strong>üóìÔ∏èAge:</strong> {user.age}</p>
      <p><strong>üè°Address:</strong> {user.address}</p>
    </div>
  );
};

export default DetailPage;
