import React, { useState, useEffect } from "react";
import { useParams, useNavigate } from "react-router-dom";

const UpdatePage = () => {
  const { id } = useParams();
  const navigate = useNavigate();
  const [user, setUser] = useState({ name: "", email: "", age: "", address: "" });

  const fetchUserDetail = async () => {
    try {
      const response = await fetch(`https://672c8aef1600dda5a9f8de2e.mockapi.io/api/v1/users/${id}`);
      const data = await response.json();
      setUser(data);
    } catch (error) {
      console.error("Error fetching user detail:", error);
    }
  };

  const handleUpdateUser = async () => {
    try {
      await fetch(`https://672c8aef1600dda5a9f8de2e.mockapi.io/api/v1/users/${id}`, {
        method: "PUT",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify(user),
      });
      navigate("/list");
    } catch (error) {
      console.error("Error updating user:", error);
    }
  };

  useEffect(() => {
    fetchUserDetail();
  }, [id]);

  return (
    <div>
      <h2>Update User</h2>
      <input value={user.name} onChange={(e) => setUser({ ...user, name: e.target.value })} />
      <input value={user.email} onChange={(e) => setUser({ ...user, email: e.target.value })} />
      <input value={user.age} onChange={(e) => setUser({ ...user, age: e.target.value })} />
      <input value={user.address} onChange={(e) => setUser({ ...user, address: e.target.value })} />
      <button onClick={handleUpdateUser}>Save</button>
    </div>
  );
};

export default UpdatePage;
